#include <stdlib.h>

int main() {
    int *ptr;
    int size = 5;

    // 메모리 할당
    ptr = (int*)malloc(size * sizeof(int));

    if (ptr == NULL) {
        printf("메모리 할당 실패\n");
        return 1;
