    int num = 29;
    bool is_prime = true;

    if (num <= 1) {
        is_prime = false;
    } else {
        for (int i = 2; i * i <= num; i++) {
            if (num % i == 0) {
                is_prime = false;
                break;
            }
        }
    }

    cout << num << "는 ";
    if (is_prime) {
        cout << "소수" << endl;
    } else {
        cout << "합성수" << endl;
    }

    // 역삼각형 별
    cout << "\n역삼각형:" << endl;
    for (int i = 5; i > 0; i--) {
