```cpp
#include <iostream>
using namespace std;

// 최댓값, 최솟값 구하기 (참조로 반환)
void get_min_max(int arr[], int size, int& min, int& max) {
    min = arr[0];
    max = arr[0];

    for (int i = 1; i < size; i++) {
        if (arr[i] < min) min = arr[i];
        if (arr[i] > max) max = arr[i];
    }
}

// 소수 판별
bool is_prime(int n) {
    if (n <= 1) return false;

    for (int i = 2; i * i <= n; i++) {
        if (n % i == 0) {
            return false;
        }
    }
    return true;
}

// 오버로딩: 절댓값
int abs(int x) {
    return (x < 0) ? -x : x;
}

double abs(double x) {
    return (x < 0) ? -x : x;
}

int main() {
    // 배열의 최솟값, 최댓값
    int numbers[] = {23, 45, 12, 67, 34};
    int min, max;

    get_min_max(numbers, 5, min, max);
    cout << "최솟값: " << min << endl;  // 12
    cout << "최댓값: " << max << endl;  // 67

    // 소수 판별
    int num = 29;
    cout << "\n" << num << "는 ";
    if (is_prime(num)) {
        cout << "소수" << endl;
    } else {
        cout << "합성수" << endl;
    }

    // 오버로딩
    cout << "\nabs(-5) = " << abs(-5) << endl;      // 5
    cout << "abs(-3.7) = " << abs(-3.7) << endl;  // 3.7

    return 0;
}
```

