```cpp
#include <iostream>
#include <memory>
using namespace std;

// 참조로 swap
void swap(int& a, int& b) {
    int temp = a;
    a = b;
    b = temp;
}

// 포인터로 배열 수정
void double_array(int* arr, int size) {
    for (int i = 0; i < size; i++) {
        arr[i] *= 2;
    }
}

int main() {
    // 참조로 swap
    int x = 10, y = 20;
    cout << "변경 전: " << x << ", " << y << endl;
    swap(x, y);
    cout << "변경 후: " << x << ", " << y << endl;

    // 동적 배열
    int size = 5;
    int* arr = new int[size]{1, 2, 3, 4, 5};

    cout << "\n원본: ";
    for (int i = 0; i < size; i++) {
        cout << arr[i] << " ";
    }
    cout << endl;

    double_array(arr, size);

    cout << "2배: ";
    for (int i = 0; i < size; i++) {
        cout << arr[i] << " ";
    }
    cout << endl;

    delete[] arr;

    // 스마트 포인터
    cout << "\n스마트 포인터:" << endl;
    unique_ptr<int> smart_ptr = make_unique<int>(100);
    cout << "값: " << *smart_ptr << endl;
    // 자동으로 메모리 해제됨

    return 0;
}
