```cpp
class Shape {
public:
    virtual double area() {  // virtual: 오버라이드 가능
        return 0;
    }
};

class Circle : public Shape {
private:
    double radius;

public:
    Circle(double r) : radius(r) {}

    double area() override {  // 오버라이드
        return 3.14159 * radius * radius;
    }
};

class Rectangle : public Shape {
private:
    double width, height;

public:
    Rectangle(double w, double h) : width(w), height(h) {}

    double area() override {
        return width * height;
    }
};

int main() {
    Circle c(5);
    Rectangle r(4, 6);

    Shape* shapes[] = {&c, &r};

    for (Shape* shape : shapes) {
        cout << "넓이: " << shape->area() << endl;
    }

    return 0;
}
```

