  // for-in으로 키 순회
  for (var name in scores.keys) {
    print('$name: ${scores[name]}점');
  }

  // for-in으로 값 순회
  for (var score in scores.values) {
    print('점수: $score');
  }

  // entries 사용
  for (var entry in scores.entries) {
    print('${entry.key}: ${entry.value}');
  }
}
```

