```java
public class Main {
    public static void main(String[] args) {
        System.out.println(calculate(10, 5, '+'));  // 15
        System.out.println(calculate(10, 5, '-'));  // 5
        System.out.println(calculate(10, 5, '*'));  // 50
        System.out.println(calculate(10, 5, '/'));  // 2
    }

    public static int calculate(int a, int b, char operator) {
        switch (operator) {
            case '+':
                return a + b;
            case '-':
                return a - b;
            case '*':
                return a * b;
            case '/':
                return b != 0 ? a / b : 0;
            default:
                return 0;
        }
    }
}
