  name: "홍길동",
  age: 25,
  city: "서울"
};

for (const key in person) {
  console.log(`${key}: ${person[key]}`);
}
// name: 홍길동
// age: 25
// city: 서울

// 배열도 가능하지만 권장하지 않음
